import logo from './logo.svg';
import './App.css';
import AddReminderForm from './components/AddReminderForm.js';
import React, { useState } from 'react';
const addReminder = ({}) =>{

}

function ReminderForm(props){
  const [formData, setFormData] = useState({title:"",start:"",end:""});

  function submitHandler(e){
    e.preventDefault();
    props.onAdd(formData);
    setFormData({title:"",start:"",end:""})
  }
  function changeHandler(e){
    setFormData({...formData,[e.target.name]: e.target.value})
  }
  return(
    <>
      <h1>John Bui's goals</h1>
      <form onSubmit={submitHandler}>
        <input type = "text" name = "title" value = {formData.title} onChange={changeHandler}></input>
        <input type = "time" name = "start" value = {formData.start} onChange={changeHandler}></input>
        <input type = "time" name = "end" value = {formData.end} onChange={changeHandler}></input>
        <button type = "submit">Submit Goal</button>
      </form>
    </>
  )
}

function ListOfGoals(props){
  return(
    <ul>
      {props.allGoals.map(
        (g)=>(
          <li key = {g.goal}>
            <p>John's goal is to {g.goal} by {g.by}</p>
          </li>
        )
      )
      }
    </ul>
  )
}

function App() {
  const [reminderList, setReminderList] = useState([]);
  return (
    <div className="App">
      <header>
        <h1 className = "title">SMART REMINDER</h1>
        <p className = "author-name">created by Nang Bui (John)</p>
      </header>
      <main>
        <ul>
          <ListOfGoals/>
        </ul>
      </main>
    </div>
  );
}

export default App;
