const formStyle = {
  width: "200px",
  height: "200px",
  display: "grid",
  textAlign: "center",
  gridTemplateRows: "1fr 1fr 1fr"
}
const submitHandler = () => {
}

function AddReminderForm({addReminder}){
    return(
      <div>
        <h1>Add a reminder?</h1>
        <form onSubmit = {submitHandler} styles = {formStyle}>
          <label>Reminder title</label>
          <input type="text" name="name"/> 
          <label>Start</label>
          <input type="datetime-local"/> 
        </form>
      </div>
    )
}

export default AddReminderForm;
